
public class Java2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a[] = { 30, 20, 10, 5, 34 };
		for (int i = 0; i < a.length; i++) {
			System.out.print(a[i] + "	");
		}
		System.out.println();
		for (int j = a.length - 1; j >= 0; j--) {
			System.out.print(a[j] + "	");
		}
	}

}
